def part1(instructions)
  replace = {"(" => 1, ")" => -1}
  instructions.split("").map{|f| replace[f]}.inject(&:+)
end

def part2(instructions)
  replace = {"(" => 1, ")" => -1}
  instructions.split("").map{|f| replace[f]}.each_with_index.inject(0) do |sum, (val, index)|
    return index if sum == -1
    sum + val
  end
end

